import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import'bootstrap/dist/css/bootstrap.css'

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
function Score(props) {
  console.log('render!');

  var [score, setScore] = useState(0);
  function increaseScore() {
    setScore(score + 1);
  }

  function decreaseScore() {
    if (score === 0) return;
    setScore(score - 1);
  }

  return (
    <div className="container">
      <p>
        Score is {score} for the {props.name}.
      </p>
      <button onClick={increaseScore}>+</button>{' '}
      <button onClick={decreaseScore}>-</button>{' '}
      <button onClick={() => setScore(0)}>Reset</button>
    </div>
  );
}

render(<Score name="Wolverines" />, document.getElementById('root'));

// STATE - 0 Starting state
// start with events
// show inline vs separate fn...
function Score0(props) {
  return (
    <div>
      <p>Score is 0 for the {props.name}.</p>
      <button>+</button> <button>-</button> <button>Reset</button>
    </div>
  );
}

// STATE - 1 Adding event function
// show inner fn
// explain () vs no () in event binding.
// show DOM. (synthetic events).
function Score1(props) {
  function increaseScore() {
    console.log('increase!');
    return 5;
  }

  return (
    <div>
      <p>Score is 0 for the {props.name}.</p>
      <button onClick={increaseScore()}>+</button> <button>-</button>{' '}
      <button>Reset</button>
    </div>
  );
}

// STATE - 2. trying to use props and inline vars to handle state
// try to use props.score++
// - props.score++ explain why this fails (from outside)
// PROPS ARE READONLY
//
// try to use inline var for state
// explain (with console.log) why it only renders once...
// show why it's updating in the console but not in the code
// RENDER(). React tries to be effiecent and only re-render
// when it detects a change in props that are passed in
// OR when state changes...
// So... HOW Does react handle state?
//
function Score2(props) {
  var score = 0;
  console.log('render');
  function increaseScore() {
    console.log('increase!');
    score++;
    console.log({ score });
  }

  return (
    <div>
      <p>
        Score is {score} for the {props.name}.
      </p>
      <button onClick={increaseScore}>+</button> <button>-</button>{' '}
      <button>Reset</button>
    </div>
  );
}

// STATE - 3. Show useState hook
// introduce the use state hook and show how it works with this simple example
// show docs?
// add deps
// - import React, {useState} from "react";
// add state
// -  var [score, setScore] = useState(0);
// explain destructuring
// modify our fn to use the hook
// EXPLAIN how calling the same thing twice results in 2 different state vars
// EXPLAIN render cycles (with render console)
// EXPLAIN setState is async

// STATE - 4. Add other buttons.
// show inline fn vs other one... (use reset?)

function Score4(props) {
  //var score = 0

  var [score, setScore] = useState(0);
  var result = useState(0);
  console.log('result', result);

  console.log('render');
  function increaseScore() {
    //console.log("increase!");
    setScore(score + 1);
    console.log('score', score);
    console.log('result[0]', result[0]);

    setTimeout(() => console.log('delayed score', score), 1000);
  }

  return (
    <div>
      <p>
        Score is {score} for the {props.name}.
      </p>
      <button onClick={increaseScore}>+</button> <button>-</button>{' '}
      <button>Reset</button>
    </div>
  );
}


// If you want to start measuring performance in your app, pass a function
