import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import'bootstrap/dist/css/bootstrap.css'
import { render } from 'react-dom';

var unitTable0 = {
  c: 'Celsius',
  f: 'Fahrenheit',
};
function TempInput0(props) {
  var [temp, setTemp] = useState(8);

  return (
    <fieldset>
      <label>{unitTable0[props.unit]}</label>
      <br />
      <input value={temp} />
    </fieldset>
  );
}

function App0() {
  return (
    <>
      <TempInput0 unit="c" />
      <TempInput0 unit="f" />
    </>
  );
}

render(<App0 />, document.getElementById('root'));


function TempInput1(props) {
  var [temp, setTemp] = useState(0);

  return (
    <fieldset>
      <label>{unitTable[props.unit]}</label>
      <br />
      <input value={temp} onChange={(e) => setTemp(e.target.value)} />
    </fieldset>
  );
}

function App2() {
  var [celsius, setCelsius] = useState(0);
  var [fahrenheit, setFahrenheit] = useState(0);

  function handleCelsiusChange(value) {
    setCelsius(value);
    // console.log('Cº changed!')
