  function handleFahrenheitChange(value) {
    setFahrenheit(value);
    // console.log('Fº changed!')
  }

  return (
    <>
      <TempInput temp={celsius} onChange={handleCelsiusChange} unit="c" />
      <TempInput temp={fahrenheit} onChange={handleFahrenheitChange} unit="f" />
    </>
  );
}

function TempInput2(props) {
  return (
    <fieldset>
      <label>{unitTable[props.unit]}</label>
      <br />
      <input
        value={props.temp}
        onChange={(e) => props.onChange(e.target.value)}
      />
    </fieldset>
  );
}

